function mailhelper_run_task($message){
  $task = new TaskResult();
  $taskToRun = $message->getTask();
  switch ($taskToRun){
    case TaskResult::TASK_CALCULATOR:
      $task->setOutput("All good");
      mailhelper_calculate($task, $message);
      break;
    default:
      $task->setOutput("Cant find this task $taskToRun");

  }
  return $task;
}

